using System.Collections;
using System.Collections.Generic;
using UnityEngine;
using System;

public class CoreLoopManager : MonoBehaviour
{
    public int currentDay = 1;
    public int maxDays = 11;
    public DailyChecklist currentChecklist;

    public event Action<int> OnDayChanged;
    public event Action<DailyChecklist> OnChecklistGenerated;

    // Start is called before the first frame update
    void Start()
    {
        StartNewDay();
    }

    public void StartNewDay()
    {
        // Increase difficulty by day
        currentChecklist = GenerateChecklistForDay(currentDay);
        OnChecklistGenerated?.Invoke(currentChecklist);
        OnDayChanged?.Invoke(currentDay);
    }

    public void SubmitInspection()
    {
        // Save results to GameData (pseudo-code)
        // DataPersistenceManager.Instance.SaveChecklistResult(currentDay, currentChecklist);
        currentDay++;
        if (currentDay > maxDays)
        {
            EndGame();
        }
        else
        {
            StartNewDay();
        }
    }

    DailyChecklist GenerateChecklistForDay(int day)
    {
        var checklist = new DailyChecklist();
        checklist.dayNumber = day;
        // Example: Increase number of items each day
        int workersToCheck = Mathf.Min(3 + day, 10);
        int equipmentToCheck = Mathf.Min(2 + day, 8);
        int constructionsToCheck = Mathf.Min(1 + day / 2, 5);
        // Generate workers
        for (int i = 0; i < workersToCheck; i++)
        {
            checklist.items.Add(new ChecklistItem {
                name = "Worker " + (i+1),
                type = ChecklistType.Worker,
                isRequired = true
            });
        }
        // Generate equipment
        for (int i = 0; i < equipmentToCheck; i++)
        {
            checklist.items.Add(new ChecklistItem {
                name = "Equipment " + (i+1),
                type = ChecklistType.Equipment,
                isRequired = true,
                requiredQuantity = UnityEngine.Random.Range(1, 4)
            });
        }
        // Generate constructions
        for (int i = 0; i < constructionsToCheck; i++)
        {
            checklist.items.Add(new ChecklistItem {
                name = "Construction " + (i+1),
                type = ChecklistType.Construction,
                isRequired = true
            });
        }
        return checklist;
    }

    void EndGame()
    {
        Debug.Log("Game Over. Show ending based on results.");
        // TODO: Calculate ending based on all days' results
    }
}

using System.Collections.Generic;

[System.Serializable]
public class DailyChecklist
{
    public int dayNumber;
    public List<ChecklistItem> items = new List<ChecklistItem>();
}
